/*
	CHANGELOG:
		11/09:
			- Fonction d'initialisation et rendu graphique commencée
			- Pixelisation de Mario et d'une brique
			- Ajout du déplacement du 'path'
			
			- Abandon de la génération aléatoire de la route pour des 'sessions', plus simple à gérer et plus sûr
	
	TODO:
		- Ajouter la 'gravité'
		- Gérer la position du 'path'
	
*/
( function(){
	var game = {
		init : function(){
			// Application des styles de 'body'
				document.body.style.width = "1000px";
				document.body.style.margin = "0 auto";
			// Création du Canvas
				canvas = document.createElement('canvas');
				ctx = canvas.getContext('2d');
			// Application des styles du canvas
				canvas.style.border = "1px solid black";
				ctx.canvas.width = 1000;
				ctx.canvas.height = 323;
				
			// Mise en place du canvas
				document.body.appendChild(canvas);
			
			// Variable de Jeu
				game.play = 1;
				game.speed = 10; // Image/Sec
				game.level = 0;
				game.session = 0;
		},
		start: function(){
			i = 0;
			var run = setInterval( function(){
				graph.clearAll();
				
					// Génération de Mario
					graph.generate(texture.mario[i%4],16,27,texture.mario.x,texture.mario.y);
					
					// Choix d'une 'session'
				
					// Génération du 'path'
					for(var l = 0; l < path.type[game.level][game.session].length; l++){
						graph.generate(texture.obstacle[path.type[game.level][game.session][l]],16,28,texture.obstacle.x[i],path.y[game.level][game.session][l]);
					}
					
					// Déplacement du 'path'
					
				i++;
			},1000/game.speed);
		}
	};
	
	// Générateur de Pixel
	var graph = {
		generate : function(imgPreData,width,height,x = 0,y = 0){
				imgData = ctx.createImageData(width,height);
				for( var i = 0; i < imgData.data.length; i+=4){
					imgData.data[i+3] = 1 << imgPreData[i/4];
				}
				ctx.putImageData(imgData,x,y);
		},
		clear : function(x1,y1,x2,y2){
			var width = x2 - x1;
			var height = y2 - y1;
			if(width <= 0) console.log("[Error]: On graph.clear - Width can't be negative.");
			if(height <= 0) console.log("[Error]: On graph.clear - Height can't be negative.");

			ctx.clearRect(x1,y1,width,height);
		},
		clearAll: function(){
			ctx.clearRect(0,0,1000,800);
			return "graph.clearAll: Done";
		}
	};
	
	// Décors pixelisés
	var texture = {
		obstacle : {
			0:"",
			1:"0000000000000000",
			x: [
				968
			],
			y: [
				580
			]
		},
		mario : {
			// Width: 16	Height: 27
			0:"",
			1:"",
			2:"",
			3:"",
			x: 968,
			y: 121
		}
	};
	
	// Différent 'path' possible
	var path = {
		type : {
			0: [
				"0000000000000000"
			],
			1: [
				"0000000000000000000",
				"0000000000000000000"
			]
		},
		y : {
			0 : [
				"4444444444444444"
			],
			1 : [
				"4565655566554566654",
				"5555555555555555555"
			]
		}
	}
		
	
	game.init();
	game.start();
}()
)